import React, {Component} from 'react';
import FormCardList from './components/FormCardList.js';
import tileData from './components/tileData';
import './App.css';

class App extends Component{

  constructor(){
    super()
    this.state={
      cards: tileData,
    }
  }
  componentDidMount() {
    fetch("http://localhost:8081/getforms")
      .then(response=> response.json())
      .then(forms => {this.setState({ cards: forms})})
      .then(console.log(this.state.cards));
  }
  onCreate(event){
    event.preventDefault();
    const newForm={
      img: "https://www.w3schools.com/w3css/img_avatar3.png",
      title: 'Form X',
      author: 'author',
    }

    return fetch('http://localhost:8081/addforms', {
        method: 'post',
        headers: {'Content-type': 'application/json'},
        body: JSON.stringify(newForm),
    }).then(response => response.json())
  }

  render(){
    return(
      <div className="App">
        <FormCardList cardlist={this.state.cards} onCreateForm={this.onCreate}/>
      </div>
    )

  }
}

export default App;
