import React, {Component} from 'react';
import FormCardList from './components/FormCardList.js';
import tileData from './components/tileData';
import './App.css';

class App extends Component{

  constructor(){
    super()
    this.state={
      cards: tileData,
    }
  }
  componentDidMount() {
    fetch("http://localhost:8081/getforms")
      .then(response=> response.json())
      .then(forms => {this.setState({ cards: forms})});
  }
  onCreate(event){
    event.preventDefault();
    console.log(event.target)
  }

  render(){
    return(
      <div className="App">
        <FormCardList cardlist={this.state.cards} onCreateForm={this.onCreate}/>
      </div>
    )

  }
}

export default App;
