import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import AddRoundedIcon from '@material-ui/icons/AddRounded';
import Modal from '@material-ui/core/Modal';
import TextField from '@material-ui/core/TextField';

useStyles = makeStyles(theme => ({
    card: {
        width: 200,
        marginTop: '30px',
        marginLeft: '30px',
    },
    mainDiv: {
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
    },
    paper: {
        position: 'absolute',
        width: 400,
        backgroundColor: theme.palette.background.paper,
        border: '2px solid #000',
        boxShadow: theme.shadows[5],
        padding: theme.spacing(2, 4, 3),
    },
    root:{
        display: 'flex',
        flexDirection: 'column',
        justifyContent: "flex-start",
    }
}));

rand=()=>{
    return Math.round(Math.random() * 20) - 10;
}

class CreateFormCard extends React.Component{
    constructor(props){
        super(props);
        this.state={
            name:'',
        }
    }

    handleOpen=()=>{
        setOpen(true);
    }

    handleClose=()=>{
        setOpen(false);
    }
    onNameChange=(event)=>{
        name=event.target.value;
    }

    submit=()=>{
        fetch('http://localhost:8081/addform', {
            method: 'post',
            headers: {'Content-type': 'application/json'},
            body: JSON.stringify({
                name: name,
            })  
        })
        .then(response=>response.json())
        .then(done=>console.log(done))
        setOpen(false);
    }

    classes = useStyles();


    render(){
        [modalStyle] = React.useState(getModalStyle);
        [open, setOpen] = React.useState(false);

        return(
            <div>
                <Card className={classes.card}>
                    <CardActionArea className={classes.mainDiv} onClick={handleOpen}>
                        <div className={classes.imgDiv}>
                        <AddRoundedIcon
                            color='secondary'
                            style={{
                                fontSize: 200
                            }}
                        />
                        </div>
                        <Typography gutterBottom variant="h5" component="h2">
                            Add Form
                        </Typography>
                    </CardActionArea>
                </Card>
                <Modal
                    aria-labelledby="simple-modal-title"
                    aria-describedby="simple-modal-description"
                    open={open}
                    onClose={handleClose}
                >
                    <div style={modalStyle} className={classes.paper}>
                    <form className={classes.root} noValidate autoComplete="off">
                        <TextField
                            name="title"
                            id="outlined-full-width"
                            label="Form Name"
                            style={{ margin: 8 }}
                            placeholder="Placeholder"
                            fullWidth
                            margin="normal"
                            InputLabelProps={{
                                shrink: true,
                            }}
                            onChange={onNameChange}
                            variant="outlined"
                        />
                        <Button size="small" variant="outlined" onClick={submit} style={{width: '100px', marginLeft:'8px'}}>Create</Button>
                    </form>
                    </div>
                </Modal>
            </div>
        )
    }
}



export default CreateFormCard;
